import React from 'react';
import { useState } from 'react';
import logo from '../Images/logo.png';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebase-config';


class Home3 extends React.Component {

  render() {

    const styles = {
      header: {
        color: 'white',
        padding: '10px',
        height: '100px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        backgroundImage: 'url(/header-bg.jpg)',
        backgroundSize: 'cover',
        boxShadow: '0px 5px 15px rgba(0,0,0,0.1)'
      },
      main: {
        padding: '20px',

      },
      footer: {
        backgroundColor: 'black',
        color: 'white',
        textAlign: 'center',
        padding: '10px',
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: 300,
        left: 0,
        bottom: 0,

      },
      nav: {
        backgroundColor: 'grey',
        color: 'black',
        textAlign: 'center',
        display: 'flex',
        flexDirection: 'row',

        justifyContent: 'right',


      },
    }
    return (
      <div>

        <Header styles={styles.header} />

        <Main styles={styles.main} />
        <Footer styles={styles.footer} />

      </div>
    );

  }
}



function Header(props) {
  return (
    <header style={props.styles}>

      <img
        src={logo}
        alt=""
        style={{ width: '250px' }}
      />








    </header>
  );
}

function Main(props) {

  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch('/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        username,
        email,
        password
      })
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          window.location = '/login';
        }
      });
  }

  const register = async () => {
    try {
      const user = await createUserWithEmailAndPassword(auth,
        email,
        password);
      console.log(user)
      window.location = '/home';
    } catch (error) {
      console.log(error.message);

    }

  };
  const [user, setUser] = useState(
    {
      Name: ''
    }
  )
  let name, value
  console.log(user)
  const data = (e) => {
    name = e.target.name;
    value = e.target.value
    setUser({ ...user, [name]: value });


  }

  const getdata = async (e) => {
    const { Nom, Prenom, groupe, Age, filiere } = user;
    e.preventDefault();
    const options = {
      method: 'POST',
      headers: {
        'content-Type': 'application/json'

      },
      body: JSON.stringify({
        Nom, Prenom, groupe, Age, filiere

      })
    }
    const res = await fetch(
      'https://polyjoule-85ad1-default-rtdb.europe-west1.firebasedatabase.app/UserData.json',
      options

    )
    console.log(res)
    window.location = '/signin2';
    if (res) {
      alert("Data sent succesfully !")
    }
    else {
      alert("Erreur Occured")
    }
  }






  return (
    <main style={props.styles}>
      <div>
        <h1>Créer un compte</h1>

        <form onSubmit={handleSubmit}>

          <label style={{ display: 'block', marginBottom: "20px" }}>
            Nom d'utilisateur:
            <input
              type="text"
              name='Nom'
              value={user.Nom}
              placeholder='Nom'
              required
              onChange={data}
            />
          </label>
          <label style={{ display: 'block', marginBottom: "20px" }}>
            Prenom d'utilisateur :
            <input
              type="text"
              name='Prenom'
              value={user.Prenom}
              placeholder='prenom'
              required
              onChange={data}
            />
          </label>
          <label style={{ display: 'block', marginBottom: "20px" }}>
            Filiere  :
            <input
              type="text"
              name='filiere'
              value={user.filiere}
              placeholder='filiere'
              required
              onChange={data}
            />
          </label>
          <label style={{ display: 'block', marginBottom: "20px" }}>
            Age  :
            <input
              type="text"
              name='Age'
              value={user.Age}
              placeholder='Enterr Age'
              required
              onChange={data}
            />
          </label>
          <label style={{ display: 'block', marginBottom: "20px" }}>
            Age  :
            <input
              type="text"
              name='groupe'
              value={user.groupe}
              placeholder='Entrer Groupe'
              required
              onChange={data}
            />
          </label>

          <button onClick={getdata}>Fatch Data</button>
        </form>
      </div>
    </main>
  );
}

function Footer(props) {

  return (
    <footer style={{
      ...props.styles,
      left: 0,
      bottom: 0,
    }} >
      <Section
        title="Nom du site"
        subtitle="Nos coordonnées"
      />

      <Section
        title="Contacts"
        subtitle="Nos coordonnées"

      />

      <Section
        title="Informations pratiques"
        subtitle="Nos coordonnées"
      />

      <Section
        title="Info sur  l'association"

      />
      <p>&copy;</p>

    </footer>
  );
}
function Section(props) {
  const sectionStyle = {
    padding: 10,

    marginRight: 10,
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center'
  }
  return (
    <div className="section" style={sectionStyle}>
      <h3>{props.title}</h3>

    </div>
  )
}






export default Home3;
