import React, { useEffect, useRef, useState } from 'react';
import { Routes, Route, BrowserRouter as Router } from 'react-router-dom';
import logo from '../Images/LogoACVA.JPG';
import { signOut } from 'firebase/auth';
import { auth } from '../firebase-config';
import Header2 from '../components/Header';
import ContactList from '../components/ContactList';
import ContactDetail from '../components/ContactDetail';
import { getContacts, saveContact, udpatePhoto } from '../api/ContactService';





class Home2 extends React.Component { 

  render() {

    const styles = {
      header: {
        color: 'white',
        padding: '10px',
        height: '100px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        backgroundImage: 'url(/header-bg.jpg)',
        backgroundSize: 'cover',
        boxShadow: '0px 5px 15px rgba(0,0,0,0.1)'
      },
      main: {
        padding: '20px', 
        
      },
      footer: {
        backgroundColor: 'black',  
        color: 'white',
        textAlign: 'center', 
        padding: '10px',
        display: 'flex',
        flexDirection: 'row', 
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: 400,
  left: 0,
  bottom: 0,
      }, 
      nav: {
        backgroundColor: 'grey',  
        color: 'black',
        textAlign: 'center', 
        display: 'flex',
        flexDirection: 'row', 

        justifyContent: 'right', 
        

      },
      
      
    }


    return (
      <div>
        <Router>
          <Header styles={styles.header} />
          <Main styles={styles.main} />
          <Footer styles={styles.footer} />
          <Routes>
            <Route
              path="/contacts"
              element={<ContactList data={data} currentPage={currentPage} getAllContacts={getAllContacts} />}
            />
            <Route
              path="/contacts/:id"
              element={<ContactDetail updateContact={updateContact} updateImage={updateImage} />}
            />
          </Routes>
        </Router>
      </div>
    );
  }
}

// partie 2 (de notre data)







function Header(props) {
