import React, { useEffect, useRef } from 'react';
import { useState } from 'react';
import logo from '../Images/LogoACVA.JPG'
import { onAuthStateChanged, signInWithEmailAndPassword, signOut } from 'firebase/auth';
import { auth } from '../firebase-config';
import DataTable from 'react-data-table-component';
import { Routes, Route, Navigate, BrowserRouter } from 'react-router-dom';

import Header2 from '../components/Header';
import ContactList from '../components/ContactList';
import ContactDetail from '../components/ContactDetail';
import { getContacts, saveContact, udpatePhoto } from '../api/ContactService';





class Home2 extends React.Component {

  render() {

    const styles = {
      header: {
        color: 'white',
        padding: '10px',
        height: '100px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        backgroundImage: 'url(/header-bg.jpg)',
        backgroundSize: 'cover',
        boxShadow: '0px 5px 15px rgba(0,0,0,0.1)'
      },
      main: {
        padding: '20px',

      },
      footer: {
        backgroundColor: 'black',
        color: 'white',
        textAlign: 'center',
        padding: '10px',
        display: 'flex',
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: 400,
        left: 0,
        bottom: 0,
      },
      nav: {
        backgroundColor: 'grey',
        color: 'black',
        textAlign: 'center',
        display: 'flex',
        flexDirection: 'row',

        justifyContent: 'right',


      },


    }


    return (

        <div>

          <Header styles={styles.header} />

          <Main styles={styles.main} />
          <Ali />


          <Footer styles={styles.footer} />


        </div>
  

    );

  }
}

// partie 2 (de notre data)







function Header(props) {
  return (
    <header style={props.styles}>

      <img
        src={logo}
        alt=""
        style={{ width: '150px' }}
      />


    </header>
  );
}
const logout = async () => {
  await signOut(auth);
  window.location = '/';
};

function Main(props) {

  const [number, setNumber] = useState(getRandomNumber());
  const [guess, setGuess] = useState('');
  const [message, setMessage] = useState('');

  function getRandomNumber() {
    return Math.floor(Math.random() * 10) + 1;
  }

  function handleSubmit(e) {
    e.preventDefault();

    if (parseInt(guess) === number) {
      setMessage("Correct! Vous avez gagné!");
      setNumber(getRandomNumber()); // generate new number
      setGuess('');
    } else {
      setMessage(guess > number ? "Trop haut!" : "Trop bas!");
      setGuess('');
    }
  }



  const column = [
